import React, { useState } from "react";
import "./App.css";

function App() {
  const [homeElo, setHomeElo] = useState("");
  const [awayElo, setAwayElo] = useState("");
  const [result, setResult] = useState(null);
  const [loading, setLoading] = useState(false);

  const handlePredict = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://3.99.219.3:5000/predict", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          home_elo: parseFloat(homeElo),
          away_elo: parseFloat(awayElo),
        }),
      });

      const data = await response.json();
      setResult(data);
    } catch (error) {
      console.error("Prediction failed:", error);
      setResult({ error: "Failed to get prediction" });
    }
    setLoading(false);
  };

  return (
    <div className="App">
      <h1>üèÄ NBA Win Predictor</h1>
      <input
        type="number"
        placeholder="Home Elo"
        value={homeElo}
        onChange={(e) => setHomeElo(e.target.value)}
      />
      <input
        type="number"
        placeholder="Away Elo"
        value={awayElo}
        onChange={(e) => setAwayElo(e.target.value)}
      />
      <button onClick={handlePredict} disabled={loading}>
        {loading ? "Predicting..." : "Predict Outcome"}
      </button>

      {result && (
        <div className="result">
          {result.error ? (
            <p style={{ color: "red" }}>{result.error}</p>
          ) : (
            <>
              <h3>Prediction: {result.prediction}</h3>
              <p>Win Probability: {Math.round(result.win_probability * 100)}%</p>
            </>
          )}
        </div>
      )}
    </div>
  );
}

export default App;
